package $6Variable_DataStructure;

public class HowCodeRun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * STRUCTURE OF JDK
		 * JAVA DEVELOPMENT KIT()
		 * {
		 * 		DEVELOPMENT TOOLS();
		 * 		JAVA RUNTIME ENVIRONMENT()
		 * 		{
		 * 			LIBRARIES();
		 * 			JAVA VIRTUAL MACHINE();
		 * 		}
		 * }
		 * 
		 * Before Compilation
		 * src()
		 * {
		 * 		SourceCode.java
		 * }
		 * 
		 * After Compilation by Compiler
		 * a bytecode.class file is generated 
		 * 
		 * Before Execution
		 * bin()
		 * {
		 * 		byteCode.class
		 * }
		 * After Execution by JVM(JAVA VIRTUAL MACHINE)
		 * a Native Code for the operating system is generated
		 * nativecode.windows
		 * nativecode.linux
		 * nativecode.mac
		 * etc.
		 * */
		System.out.println(
				 "STRUCTURE OF JDK\n* JAVA DEVELOPMENT KIT()\n* {\n* 		DEVELOPMENT TOOLS();\n* 		JAVA RUNTIME ENVIRONMENT()\n* 		{\n* 			LIBRARIES();\n* 			JAVA VIRTUAL MACHINE();\n* 		}\n* }\n* \n* Before Compilation\n* src()\n* {\n* 		SourceCode.java\n* }\n* \n* After Compilation by Compiler\n* a bytecode.class file is generated\n* \n* Before Execution\n* bin()\n* {\n* 		byteCode.class\n* }\n* After Execution by JVM(JAVA VIRTUAL MACHINE)\n* a Native Code for the operating system is generated\n* nativecode.windows\n* nativecode.linux\n* nativecode.mac\n* etc.");
	}

}
