package $6Variable_DataStructure;

public class Output {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//print() doesn't create a new line after print
		System.out.print("Hello World");
		//println() create a new line after prin
		System.out.println("Hello World");
		//\n can also be used to create a new line
		System.out.println("Hello World\n");
		//\n can be put anywhere in the print statement
		System.out.println("Hello \n World");
		//\t can be used to create tab space
		System.out.println("Hello\tWorld");

	}

}
