package $6Variable_DataStructure;

public class PrimitiveDataTypes {
	/*
	 * JAVA is a typed language in which we define the type of data type during
	 * compile time or execution time
	 * Primitive Datatype size is fixed 
	 * eg:byte,short,int,char,float,double,boolean,long.
	 * Non Primitive Datatype size is not fixed
	 * eg:String,Array,Class,Object,Interface
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true;
		boolean b=false;
		System.out.println(a);
		System.out.println(b);
		byte c=8;
		System.out.println(c);
		short n=24;
		System.out.println(n);
		char ch='A';
		System.out.println(ch);
		int i=25;
		System.out.println(i);
		float f=9.87f;
		System.out.println(f);
		long l=879L;
		System.out.println(l);
		double d=879.123;
		System.out.println(d);
		

	}

}
